<?phpnamespace Admin\Controller;use Think\Controller;class ClearController extends BaseController {	public  function index() {		$this->display();	}	public function dirDel($path){		if(!is_dir($path)){			$this->error($path."输入的不是有效目录");		}		$hand = opendir($path);		while(($file = readdir($hand))!==false){			if($file=="."||$file=="..")  continue;			if(is_dir($path."/".$file)){				$this->dirDel($path."/".$file);			}else{				@unlink($path."/".$file);			}			}		closedir($hand);		@rmdir($path);	}	// 	清理thinkphp的缓存	public function doClear(){		$path = RUNTIME_PATH;		$data = $_POST['data'];		$count = count($data);		for ($i=0; $i <$count ; $i++) {			$this->dirDel($path.$data[$i]."/");		}		$this->success("清除成功");	}}